// App.js - Hovedkomponenten for Nyskolen Posten
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css';
import Hjem from './components/Hjem';
import OmOss from './components/OmOss';
import NyArtikkel from './components/NyArtikkel';
import ArtikkelVisning from './components/ArtikkelVisning';
import Login from './components/Login';
import Register from './components/Register';
import MineArtikler from './components/MineArtikler';
import AdminPanel from './components/AdminPanel';

function App() {
  const [bruker, setBruker] = useState(null);
  const [artikler, setArtikler] = useState([]);
  const [brukere, setBrukere] = useState([]);

  // Hent data fra localStorage og opprett admin-bruker hvis nødvendig
  useEffect(() => {
    // Hent lagret bruker
    const lagretBruker = localStorage.getItem('bruker');
    if (lagretBruker) {
      setBruker(JSON.parse(lagretBruker));
    }

    // Hent lagrede artikler
    const lagretArtikler = localStorage.getItem('artikler');
    if (lagretArtikler) {
      setArtikler(JSON.parse(lagretArtikler));
    } else {
      localStorage.setItem('artikler', JSON.stringify([]));
    }

    // Hent lagrede brukere
    const lagretBrukere = localStorage.getItem('brukere');
    if (lagretBrukere) {
      setBrukere(JSON.parse(lagretBrukere));
    } else {
      // Opprett admin-bruker hvis ingen brukere finnes
      const adminBruker = {
        id: 'admin-' + Date.now().toString(36),
        navn: 'Administrator',
        epost: 'admin@nyskolen.no',
        passord: 'admin123',
        rolle: 'admin',
        godkjent: true,
        klasse: 'Lærer',
        dato: new Date().toISOString()
      };

      setBrukere([adminBruker]);
      localStorage.setItem('brukere', JSON.stringify([adminBruker]));
      console.log('Admin-bruker opprettet:', adminBruker);
    }
  }, []);

  // Håndterer innlogging
  const handleLogin = (brukerInfo) => {
    setBruker(brukerInfo);
    localStorage.setItem('bruker', JSON.stringify(brukerInfo));
  };

  const handleLogout = () => {
    setBruker(null);
    localStorage.removeItem('bruker');
  };

  // Registrerer ny bruker
  const registrerBruker = (nyBruker) => {
    // Sjekk om epost allerede er registrert
    if (brukere.some(b => b.epost === nyBruker.epost)) {
      return false; // Epost allerede i bruk
    }

    const brukerMedInfo = {
      ...nyBruker,
      id: Date.now().toString(),
      godkjent: true, // For testing setter vi godkjent til true
      dato: new Date().toISOString()
    };

    const oppdaterteBrukere = [...brukere, brukerMedInfo];
    setBrukere(oppdaterteBrukere);
    localStorage.setItem('brukere', JSON.stringify(oppdaterteBrukere));

    return true; // Registrering vellykket
  };

  // Oppdater bruker
  const oppdaterBruker = (oppdatertBruker) => {
    const oppdaterteBrukere = brukere.map(bruker => 
      bruker.id === oppdatertBruker.id ? oppdatertBruker : bruker
    );
    setBrukere(oppdaterteBrukere);
    localStorage.setItem('brukere', JSON.stringify(oppdaterteBrukere));
  };

  // Slett bruker
  const slettBruker = (brukerId) => {
    const oppdaterteBrukere = brukere.filter(bruker => bruker.id !== brukerId);
    setBrukere(oppdaterteBrukere);
    localStorage.setItem('brukere', JSON.stringify(oppdaterteBrukere));
  };

  // Legg til ny artikkel
  const leggTilArtikkel = (nyArtikkel) => {
    if (!bruker) return false;

    // Legger til bruker og dato til artikkelen
    const artikkelMedInfo = {
      ...nyArtikkel,
      forfatterNavn: bruker.navn,
      forfatterID: bruker.id,
      dato: new Date().toISOString(),
      id: Date.now().toString(),
      status: 'publisert' // For testing setter vi status til publisert
    };

    // Legger til i listen over artikler
    const oppdaterteArtikler = [artikkelMedInfo, ...artikler];
    setArtikler(oppdaterteArtikler);
    localStorage.setItem('artikler', JSON.stringify(oppdaterteArtikler));

    return artikkelMedInfo.id;
  };

  // Oppdater artikkel
  const oppdaterArtikkel = (oppdatertArtikkel) => {
    const oppdaterteArtikler = artikler.map(artikkel => 
      artikkel.id === oppdatertArtikkel.id ? oppdatertArtikkel : artikkel
    );
    setArtikler(oppdaterteArtikler);
    localStorage.setItem('artikler', JSON.stringify(oppdaterteArtikler));
  };

  // Sletter en artikkel
  const slettArtikkel = (artikkelId) => {
    const oppdaterteArtikler = artikler.filter(artikkel => artikkel.id !== artikkelId);
    setArtikler(oppdaterteArtikler);
    localStorage.setItem('artikler', JSON.stringify(oppdaterteArtikler));
  };

  return (
    <Router>
      <div className="app">
        <header className="header">
          <div className="logo-container">
            <h1>Nyskolen Posten</h1>
            <p className="slogan">Elevdrevet avis for Nyskolen i Oslo</p>
          </div>
          <nav className="navigation">
            <Link to="/">Forsiden</Link>
            <Link to="/om-oss">Om Nyskolen Posten</Link>
            {bruker ? (
              <>
                <Link to="/ny-artikkel">Skriv ny artikkel</Link>
                <Link to="/mine-artikler">Mine artikler</Link>
                {bruker.rolle === 'admin' && 
                  <Link to="/admin">Administrer</Link>
                }
                <button onClick={handleLogout} className="logout-button">Logg ut ({bruker.navn})</button>
              </>
            ) : (
              <>
                <Link to="/login" className="login-button">Logg inn</Link>
                <Link to="/register" className="register-button">Registrer deg</Link>
              </>
            )}
          </nav>
        </header>

        <main className="main-content">
          <Routes>
            <Route path="/" element={<Hjem artikler={artikler.filter(a => a.status === 'publisert')} />} />
            <Route path="/om-oss" element={<OmOss />} />
            <Route path="/ny-artikkel" element={
              bruker ? 
                <NyArtikkel leggTilArtikkel={leggTilArtikkel} /> : 
                <Login onLogin={handleLogin} melding="Du må logge inn for å skrive artikler" brukere={brukere} />
            } />
            <Route path="/artikkel/:id" element={<ArtikkelVisning artikler={artikler} />} />
            <Route path="/mine-artikler" element={
              bruker ? 
                <MineArtikler 
                  innloggetBruker={bruker}
                  artikler={artikler} 
                  onDeleteArticle={slettArtikkel}
                  onUpdateArticle={oppdaterArtikkel}
                /> : 
                <Login onLogin={handleLogin} melding="Du må logge inn for å se dine artikler" brukere={brukere} />
            } />
            <Route path="/admin" element={
              bruker && bruker.rolle === 'admin' ? 
                <AdminPanel 
                  innloggetBruker={bruker}
                  brukere={brukere} 
                  artikler={artikler} 
                  onDeleteArticle={slettArtikkel}
                  onUpdateArticle={oppdaterArtikkel}
                  onUpdateUser={oppdaterBruker}
                  onDeleteUser={slettBruker}
                /> : 
                <Hjem artikler={artikler.filter(a => a.status === 'publisert')} />
            } />
            <Route path="/login" element={<Login onLogin={handleLogin} brukere={brukere} />} />
            <Route path="/register" element={<Register onRegister={registrerBruker} brukere={brukere} />} />
          </Routes>
        </main>

        <footer className="footer">
          <div className="footer-content">
            <p>&copy; {new Date().getFullYear()} Nyskolen Posten. Alle rettigheter reservert.</p>
          </div>
        </footer>
      </div>
    </Router>
  );
}

export default App;
